mapper.sql:
WHERE 1=1
     AND id = #{id}
     AND user_name LIKE CONCAT('%','${userName}','%')
     AND company_id = #{companyId}

mapper.select:
select 

from sys_user
where user_name = #{userName,jdbcType=VARCHAR}
and
password = #{password,jdbcType=VARCHAR}

mapper.insert:
insert into sys_user (id, user_name, password, 
  is_enable, role_code, remark, 
  creator, createTime, modifier, 
  modifyTime, rec_ver, company_id
  )
values (#{id,jdbcType=INTEGER}, #{userName,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{isEnable,jdbcType=VARCHAR}, #{roleCode,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}, 
  #{creator,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}, #{modifier,jdbcType=INTEGER}, 
  #{modifytime,jdbcType=TIMESTAMP}, #{recVer,jdbcType=INTEGER}, #{companyId,jdbcType=INTEGER}
  )
